import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import TextField from "@material-ui/core/TextField";
import FormLabel from "@material-ui/core/FormLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Radio from "@material-ui/core/Radio";
import DialogActions from "@material-ui/core/DialogActions";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import React, {useState} from "react";

export default function(props){
    const [open,setOpen] = useState(props.isOpen);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState(null);
    const [role, setRole] = useState("");

    const onSubmit = () => {
        let data = {
            name: name,
            email_id: email,
            mobile_no: phone,
            role_type: role
        };

        const url = URL.users;
        fetch(url,{
            method : 'POST',
            body : JSON.stringify(data),
            headers : {
                "Content-Type": "application/json"
            }
        }).then(response => {
            if(response.state === 201){
                return response.json();
            }
            return response.error;
        }).then(responseJson => {
            console.log(responseJson);
        }).catch(error => console.log(error));
    }
    return (
        <Dialog open={open} onClose={setOpen(false)} aria-labelledby="form-dialog-title">
            <DialogTitle id="form-dialog-title">Add New User</DialogTitle>
            <DialogContent>
                <TextField
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Full Name*"
                    type="text"
                    fullWidth
                    onChange={(event => setName(event.target.value))}
                />

            </DialogContent>
            <DialogContent>
                <TextField
                    margin="dense"
                    id="email"
                    label="Email ID*"
                    type="email"
                    fullWidth
                    onChange={(event => setEmail(event.target.value))}
                />
            </DialogContent>
            <FormLabel style={{justifyContent: "center", padding: 30}}>Role Type*</FormLabel>
            <RadioGroup disabled aria-label="Roll Type" name="type" style={{
                padding: '8px 24px'
            }}>
                <FormControlLabel
                    value="Admin"
                    control={<Radio color="primary"/>}
                    label="Admin"
                    onClick={() => setRole('Admin')}
                />
                <FormControlLabel
                    value="Customer Executive"
                    control={<Radio color="primary"/>}
                    label="Customer Executive"
                    onClick={() => setRole('Customer Executive')}
                />
            </RadioGroup>
            <DialogContent>
                <TextField
                    margin="dense"
                    id="phone"
                    label="Phone number"
                    type="text"
                    fullWidth
                    onChange={event => setPhone(event.target.value)}
                />
            </DialogContent>
            <DialogActions>
                <Button onClick={setOpen(false)} color="primary">
                    Cancel
                </Button>
                <Button onClick={onSubmit} color="primary">
                    Register
                </Button>
            </DialogActions>
        </Dialog>
    )
}
