import React from 'react';
import {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
// import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Radio from "@material-ui/core/Radio";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import RadioGroup from "@material-ui/core/RadioGroup";


export default function RegisterUser() {

  const [open, setOpen] = useState(false);
  const [name, setName]  = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");

  const onChangeName = (event) => {
    setName({...name,[event.target.name]: event.target.value});
  }
  const onChangeEmail = (event) => {
    setEmail({...email,[event.target.email]: event.target.value});
  }
  const onChangePhone = (event) => {
    setPhone({...phone,[event.target.phone]: event.target.value});
  }

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Add User
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add New User</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Full Name"
            type="text"
            fullWidth
            onChange={onChangeName}
          />

        </DialogContent>
        <DialogContent>
          <TextField
            margin="dense"
            id="email"
            label="Email ID"
            type="email"
            fullWidth
            onChange={onChangeEmail}
          />
        </DialogContent>
          <FormLabel style = {{justifyContent : "center", padding : 30}} >Role Type</FormLabel>
            <RadioGroup disabled aria-label="Roll Type" name="type" >
              <FormControlLabel
                  value="admin"
                  control={<Radio color = "primary" />}
                  label="Admin"
              />
              <FormControlLabel
                  value="Customer Executive"
                  control={<Radio color = "primary" />}
                  label="Customer Executive"
              />
            </RadioGroup>
        <DialogContent>
          <TextField
            margin="dense"
            id="phone"
            label="Phone number"
            type="text"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Register
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
